const gulp = require("gulp");
const gulp_imagemins = require("gulp-imagemin");


function MinifyImages(){

    return gulp.src("./ns/design/imgs/unsplash/largeimages/*.jpg")
        .pipe(gulp_imagemins())
        .pipe(gulp.dest("./ns/design/imgs/unsplash/minified"))
};

exports.MINIFY_IMAGES = MinifyImages;